SELECT rtrim(left(coalesce(CAST(CPF_CNPJ_Cliente__c as char(255)),'<null>'), 255)) as CPF_CNPJ_Cliente__c, rtrim(left(coalesce(CAST(HC_CPF_CNPJ_Codigo_Parceiro_Unico__c as char(255)),'<null>'), 255)) as HC_CPF_CNPJ_Codigo_Parceiro_Unico__c, rtrim(left(coalesce(CAST(HC_CPF_CNPJ__c as char(255)),'<null>'), 255)) as HC_CPF_CNPJ__c, rtrim(left(coalesce(CAST(Id as char(255)),'<null>'), 255)) as Id, rtrim(left(coalesce(CAST(HC_Parceiro__c as char(255)),'<null>'), 255)) as HC_Parceiro__c from HC_Relacao_Cliente_Parceiro__c_SALESFORCE
WHERE HC_CPF_CNPJ_Codigo_Parceiro_Unico__c not like '%' + CPF_CNPJ_Cliente__c + '%'